import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink,
} from "react-router-dom";
import WelcomePage from "./pages/WelcomePage";
import HomePage from "./pages/HomePage";
import LogDetailsPage from "./pages/LogDetailsPage";
import AddEditLogPage from "./pages/AddEditLogPage";
import CommentsPage from "./pages/CommentsPage";
import { ToastContainer } from "react-toastify";
import "./App.css";
import RecommendedLogsPage from "./pages/RecommendedLogsPage";

const App = () => {
  return (
    <Router>
      <nav className="menu">
        <ul className="menu-list">
          <li>
            <NavLink
              to="/home"
              className="menu-link"
              style={({ isActive }) =>
                isActive ? { fontWeight: "bold", color: "red" } : {}
              }
            >
              All Logs
            </NavLink>
          </li>
          <li className="menu-icon">
            <NavLink to="/">
              <img src="/toast.png" alt="Welcome" className="welcome-icon" />
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/add"
              className="menu-link"
              style={({ isActive }) =>
                isActive ? { fontWeight: "bold", color: "red" } : {}
              }
            >
              Add Log
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/recommended"
              className="menu-link"
              style={({ isActive }) =>
                isActive ? { fontWeight: "bold", color: "red" } : {}
              }
            >
              Recommended
            </NavLink>
          </li>
        </ul>
      </nav>

      <ToastContainer
        position="top-right"
        autoClose={7000}
        hideProgressBar={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
        transition="bounce"
      />

      <Routes>
        <Route path="/" element={<WelcomePage />} />
        <Route path="/home" element={<HomePage />} />
        <Route path="/recommended" element={<RecommendedLogsPage />} />

        <Route path="/logs/:id" element={<LogDetailsPage />}>
          <Route path="comments" element={<CommentsPage />} />
        </Route>

        <Route path="/add" element={<AddEditLogPage />} />
      </Routes>
    </Router>
  );
};

export default App;
