import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import LogItem from "../components/LogItem";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const LogDetailsPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [log, setLog] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [users, setUsers] = useState([]);
  const [diningOptions, setDiningOptions] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [newRecommendation, setNewRecommendation] = useState("");

  const [formValues, setFormValues] = useState({
    foodOrdered: "",
    date: "",
    rating: "",
    description: "",
    diningOption: [],
  });

  const apiKey = process.env.REACT_APP_API_KEY;

  const fetchData = () => {
    fetch(`http://localhost:5500/logs/${id}`, {
      headers: {
        Authorization: `Bearer ${apiKey}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setLog(data);
        setFormValues({
          foodOrdered: data.foodOrdered,
          date: data.date,
          rating: data.rating,
          description: data.description,
          diningOption: data.diningOption || [],
        });
      })
      .catch(() => toast.error("Failed to load log details."));

    fetch("http://localhost:5500/diningOption", {
      headers: {
        Authorization: `Bearer ${apiKey}`,
      },
    })
      .then((res) => res.json())
      .then((data) => setDiningOptions(data))
      .catch(() => toast.error("Failed to load dining options."));

    fetch("http://localhost:5500/users", {
      headers: {
        Authorization: `Bearer ${apiKey}`,
      },
    })
      .then((res) => res.json())
      .then((data) => setUsers(data))
      .catch(() => toast.error("Failed to load users."));

    fetch("http://localhost:5500/restaurants", {
      headers: {
        Authorization: `Bearer ${apiKey}`,
      },
    })
      .then((res) => res.json())
      .then((data) => setRestaurants(data))
      .catch(() => toast.error("Failed to load restaurants."));
  };

  useEffect(() => {
    if (log === null) {
      fetchData();
    }
  }, [log]);

  const getRestaurantName = (restaurantId) => {
    const restaurant = restaurants.find((res) => res.id === restaurantId);
    return restaurant ? restaurant.name : "Unknown Restaurant";
  };

  const handleSaveChanges = () => {
    const updatedLog = {
      restaurantId: log.restaurantId,
      foodOrdered: formValues.foodOrdered,
      date: formValues.date,
      rating: formValues.rating,
      description: formValues.description,
      diningOption: formValues.diningOption,
    };

    fetch(`http://localhost:5500/logs/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${apiKey}`,
      },
      body: JSON.stringify(updatedLog),
    })
      .then((res) => {
        if (!res.ok) {
          toast.error("Failed to save changes.");
          return;
        }
        return res.json();
      })
      .then((updatedLog) => {
        if (updatedLog) {
          setLog(updatedLog);
          setIsEditing(false);
          toast.success("Changes saved successfully!");
        }
      })
      .catch(() => toast.error("Failed to save changes."));
  };

  const handleUpdateRecommendation = () => {
    if (newRecommendation === "") {
      toast.error("Please select a recommendation.");
      return;
    }

    const updatedRecommendValue = newRecommendation === "true";

    fetch(`http://localhost:5500/logs/${id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${apiKey}`,
      },
      body: JSON.stringify({ recommend: updatedRecommendValue }),
    })
      .then((res) => {
        if (!res.ok) {
          toast.error("Failed to update recommendation.");
        }
        return res.json();
      })
      .then((updatedLog) => {
        setLog(updatedLog);
        setNewRecommendation("");
        toast.success("Recommendation updated successfully!");
      })
      .catch(() => toast.error("Failed to update recommendation."));
  };

  if (!log) return <p>Loading log details...</p>;

  return (
    <div className="container">
      <h1>{isEditing ? "Edit Log" : "Log Details"}</h1>
      {isEditing ? (
        <form>
          <p>
            <strong>Restaurant:</strong> {getRestaurantName(log.restaurantId)}
          </p>

          <div className="mb-3">
            <label>Food Ordered</label>
            <input
              type="text"
              value={formValues.foodOrdered}
              onChange={(e) =>
                setFormValues({ ...formValues, foodOrdered: e.target.value })
              }
              className="form-control"
            />
          </div>

          <div className="mb-3">
            <label>Date</label>
            <input
              type="date"
              value={formValues.date}
              onChange={(e) =>
                setFormValues({ ...formValues, date: e.target.value })
              }
              className="form-control"
            />
          </div>

          <div className="mb-3">
            <label>Rating</label>
            <select
              value={formValues.rating}
              onChange={(e) =>
                setFormValues({ ...formValues, rating: e.target.value })
              }
              className="form-select"
            >
              <option value="">Select Rating</option>
              {[1, 2, 3, 4, 5].map((rating) => (
                <option key={rating} value={rating}>
                  {rating}
                </option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label>Description</label>
            <textarea
              value={formValues.description}
              onChange={(e) =>
                setFormValues({ ...formValues, description: e.target.value })
              }
              className="form-control"
            />
          </div>

          <div className="mb-3">
            <label>Dining Option</label>
            <div>
              {diningOptions.map((option) => (
                <label key={option.id} className="me-3">
                  <input
                    type="checkbox"
                    value={option.id}
                    checked={formValues.diningOption.includes(option.id)}
                    onChange={(e) => {
                      const isChecked = e.target.checked;
                      if (isChecked) {
                        setFormValues({
                          ...formValues,
                          diningOption: [...formValues.diningOption, option.id],
                        });
                      } else {
                        setFormValues({
                          ...formValues,
                          diningOption: formValues.diningOption.filter(
                            (id) => id !== option.id
                          ),
                        });
                      }
                    }}
                  />
                  {option.name}
                </label>
              ))}
            </div>
          </div>

          <button
            type="button"
            className="btn btn-success"
            onClick={handleSaveChanges}
          >
            Save Changes
          </button>
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => setIsEditing(false)}
          >
            Cancel
          </button>
        </form>
      ) : (
        <>
          <LogItem
            log={log}
            users={users}
            restaurants={restaurants}
            diningOptions={diningOptions}
          />
          <div className="mb-3">
            <div className="recommendation-options">
              <div>
                <label>
                  <input
                    type="radio"
                    name="recommend"
                    value="true"
                    checked={newRecommendation === "true"}
                    onChange={(e) => setNewRecommendation(e.target.value)}
                  />{" "}
                  Recommend
                </label>
              </div>
              <div>
                <label>
                  <input
                    type="radio"
                    name="recommend"
                    value="false"
                    checked={newRecommendation === "false"}
                    onChange={(e) => setNewRecommendation(e.target.value)}
                  />{" "}
                  Don't Recommend
                </label>
              </div>
            </div>
            <br />

            <button
              className="btn btn-primary mt-2"
              onClick={handleUpdateRecommendation}
            >
              Update Recommendation
            </button>
          </div>

          <button
            className="btn btn-warning me-2"
            onClick={() => setIsEditing(true)}
          >
            Edit
          </button>

          <button
            className="btn btn-danger me-2"
            onClick={() => {
              if (window.confirm("Are you sure you want to delete this log?")) {
                fetch(`http://localhost:5500/logs/${id}`, {
                  method: "DELETE",
                  headers: {
                    Authorization: `Bearer ${apiKey}`,
                  },
                })
                  .then((res) => res.json())
                  .then(() => {
                    toast.success("Log deleted.");
                    navigate("/home");
                  })
                  .catch(() => toast.error("Failed to delete log."));
              }
            }}
          >
            Delete
          </button>
        </>
      )}
    </div>
  );
};

export default LogDetailsPage;
