import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import DropdownWithNewItem from "../components/DropdownWithNewItem";

const AddEditLogPage = () => {
  const navigate = useNavigate();
  const hasFetchedData = useRef(false); // To control the data fetch only once
  const [diningOptions, setDiningOptions] = useState([]);
  const [users, setUsers] = useState([]);
  const [restaurants, setRestaurants] = useState([]);
  const [restaurantId, setRestaurantId] = useState("");
  const [userId, setUserId] = useState("");
  const [foodOrdered, setFoodOrdered] = useState("");
  const [date, setDate] = useState("");
  const [rating, setRating] = useState("");
  const [description, setDescription] = useState("");
  const [diningOption, setDiningOption] = useState([]);
  const [recommend, setRecommend] = useState(null);
  const [errors, setErrors] = useState({});

  const apiKey = process.env.REACT_APP_API_KEY;

  const fetchData = () => {
    if (hasFetchedData.current) return;

    const headers = {
      Authorization: `Bearer ${apiKey}`,
    };

    // Fetch data for users, restaurants, and dining options
    fetch("http://localhost:5500/users", { headers })
      .then((response) => response.json())
      .then((data) => setUsers(data))
      .catch(() => toast.error("Failed to load users."));

    fetch("http://localhost:5500/restaurants", { headers })
      .then((response) => response.json())
      .then((data) => setRestaurants(data))
      .catch(() => toast.error("Failed to load restaurants."));

    fetch("http://localhost:5500/diningOption", { headers })
      .then((response) => response.json())
      .then((data) => setDiningOptions(data))
      .catch(() => toast.error("Failed to load dining options."));

    hasFetchedData.current = true; // Mark data as fetched
  };

  const handleDiningOptionChange = (e) => {
    const optionId = e.target.value;
    let updatedDiningOption = diningOption.slice();

    if (updatedDiningOption.indexOf(optionId) === -1) {
      updatedDiningOption.push(optionId); // Use push() to add
    } else {
      updatedDiningOption.splice(updatedDiningOption.indexOf(optionId), 1);
    }

    setDiningOption(updatedDiningOption);
  };

  const validateForm = () => {
    const newErrors = {};
    if (!restaurantId) newErrors.restaurant = "Please select a restaurant.";
    if (!userId) newErrors.user = "Please select a user.";
    if (!foodOrdered) newErrors.foodOrdered = "Food ordered is required.";
    if (!date) newErrors.date = "Date is required.";
    if (!rating) newErrors.rating = "Rating is required.";
    if (!description) newErrors.description = "Description is required.";
    if (diningOption.length === 0)
      newErrors.diningOption = "At least one dining option is required.";
    if (recommend === null) newErrors.recommend = "Recommendation is required.";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    const logData = {
      restaurantId,
      userId,
      foodOrdered,
      date,
      rating,
      description,
      diningOption,
      recommend,
    };

    const headers = {
      "Content-Type": "application/json",
      Authorization: `Bearer ${apiKey}`,
    };

    // Check if this is an "Edit" or "Add" operation
    if (restaurantId && userId) {
      fetch("http://localhost:5500/logs", {
        method: "POST",
        headers,
        body: JSON.stringify(logData),
      })
        .then((res) => res.json())
        .then(() => {
          toast.success("Log added successfully!");
          navigate("/home");
        })
        .catch((error) => {
          toast.error("Failed to add log.");
          console.error(error);
        });
    }
  };

  // Manually call the fetch data
  fetchData();

  return (
    <div className="container">
      <h1 data-testid="header">✍️ Add New Log</h1>
      <form onSubmit={handleSubmit} data-testid="log-form">
        <DropdownWithNewItem
          label="User"
          apiEndpoint="http://localhost:5500/users"
          selectedId={userId}
          setSelectedId={setUserId}
          data-testid="user-dropdown"
          apiKey={apiKey}
        />

        <DropdownWithNewItem
          label="Restaurant"
          apiEndpoint="http://localhost:5500/restaurants"
          selectedId={restaurantId}
          setSelectedId={setRestaurantId}
          data-testid="restaurant-dropdown"
          apiKey={apiKey}
        />

        <div className="mb-3">
          <label
            className="form-label"
            htmlFor="foodOrdered"
            data-testid="food-label"
          >
            Food Ordered
          </label>
          <input
            type="text"
            id="foodOrdered"
            value={foodOrdered}
            onChange={(e) => setFoodOrdered(e.target.value)}
            className="form-control"
            data-testid="food-ordered-input"
          />
          {errors.foodOrdered && (
            <div className="text-danger" data-testid="food-error">
              {errors.foodOrdered}
            </div>
          )}
        </div>

        <div className="mb-3">
          <label className="form-label" htmlFor="date" data-testid="date-label">
            Date
          </label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="form-control"
            data-testid="date-input"
          />
          {errors.date && (
            <div className="text-danger" data-testid="date-error">
              {errors.date}
            </div>
          )}
        </div>

        <div className="mb-3">
          <label
            className="form-label"
            htmlFor="rating"
            data-testid="rating-label"
          >
            Rating
          </label>
          <select
            id="rating"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            className="form-select"
            data-testid="rating-select"
          >
            <option value="">-- Select Rating --</option>
            {[1, 2, 3, 4, 5].map((ratingValue) => (
              <option key={ratingValue} value={ratingValue}>
                {ratingValue}
              </option>
            ))}
          </select>
          {errors.rating && (
            <div className="text-danger" data-testid="rating-error">
              {errors.rating}
            </div>
          )}
        </div>

        <div className="mb-3">
          <label
            className="form-label"
            htmlFor="description"
            data-testid="description-label"
          >
            Description
          </label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="form-control"
            rows="3"
            data-testid="description-textarea"
          />
          {errors.description && (
            <div className="text-danger" data-testid="description-error">
              {errors.description}
            </div>
          )}
        </div>

        <div className="mb-3">
          <label className="form-label" data-testid="dining-option-label">
            Dining Options
          </label>
          <div data-testid="dining-options">
            {diningOptions.map((option) => (
              <div key={option.id}>
                <label>
                  <input
                    type="checkbox"
                    value={option.id}
                    checked={diningOption.indexOf(option.id) !== -1}
                    onChange={handleDiningOptionChange}
                    data-testid={`dining-option-${option.id}`}
                  />{" "}
                  {option.name}
                </label>
              </div>
            ))}
          </div>
          {errors.diningOption && (
            <div className="text-danger" data-testid="dining-option-error">
              {errors.diningOption}
            </div>
          )}
        </div>

        <div className="mb-3">
          <label className="form-label" data-testid="recommendation-label">
            Would You Recommend?
          </label>
          <div>
            <label>
              <input
                type="radio"
                name="recommend"
                value={true}
                checked={recommend === true}
                onChange={() => setRecommend(true)}
                data-testid="recommend-yes"
              />{" "}
              Yes
            </label>
            <label>
              <input
                type="radio"
                name="recommend"
                value={false}
                checked={recommend === false}
                onChange={() => setRecommend(false)}
                data-testid="recommend-no"
              />{" "}
              No
            </label>
          </div>
          {errors.recommend && (
            <div className="text-danger" data-testid="recommend-error">
              {errors.recommend}
            </div>
          )}
        </div>

        <button
          type="submit"
          className="btn btn-primary"
          data-testid="submit-button"
        >
          Submit Log
        </button>
      </form>
    </div>
  );
};

export default AddEditLogPage;
