import React, { useState, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import LogItem from "../components/LogItem";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const LogDetailsPage = ({}) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [log, setLog] = useState(null);
  const [restaurants, setRestaurants] = useState([]);
  const [users, setUsers] = useState([]);
  const [diningOptions, setDiningOptions] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [newRecommendation, setNewRecommendation] = useState("");

  const logRef = useRef(null);
  const restaurantRef = useRef([]);
  const usersRef = useRef([]);
  const diningOptionsRef = useRef([]);

  const fetchData = () => {
    fetch(`http://localhost:5500/logs/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setLog(data);
        logRef.current = data;
      })
      .catch(() => toast.error("Failed to load log details."));

    fetch("http://localhost:5500/diningOption")
      .then((res) => res.json())
      .then((data) => {
        setDiningOptions(data);
        diningOptionsRef.current = data;
      })
      .catch(() => toast.error("Failed to load dining options."));

    fetch("http://localhost:5500/users")
      .then((res) => res.json())
      .then((data) => {
        setUsers(data);
        usersRef.current = data;
      })
      .catch(() => toast.error("Failed to load users."));

    fetch("http://localhost:5500/restaurants")
      .then((res) => res.json())
      .then((data) => {
        setRestaurants(data);
        restaurantRef.current = data;
      })
      .catch(() => toast.error("Failed to load restaurants."));
  };

  useEffect(() => {
    if (log) {
      document.title = `Log Details - ${log.foodOrdered}`;
    }
    if (log === null) {
      fetchData();
    }
  }, [log]);

  const getRestaurantName = (restaurantId) => {
    const restaurant = restaurantRef.current.find(
      (res) => res.id === restaurantId
    );
    return restaurant ? restaurant.name : "Unknown Restaurant";
  };

  const handleSaveChanges = () => {
    const updatedLog = {
      restaurantId: logRef.current.restaurantId,
      foodOrdered: logRef.current.foodOrdered,
      date: logRef.current.date,
      rating: logRef.current.rating,
      description: logRef.current.description,
      diningOption: logRef.current.diningOption,
    };

    fetch(`http://localhost:5500/logs/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(updatedLog),
    })
      .then((res) => {
        if (!res.ok) {
          toast.error("Failed to save changes.");
          return;
        }
        return res.json();
      })
      .then((updatedLog) => {
        if (updatedLog) {
          setLog(updatedLog);
          setIsEditing(false);
          toast.success("Changes saved successfully!");
        }
      })
      .catch(() => toast.error("Failed to save changes."));
  };

  const handleUpdateRecommendation = () => {
    if (newRecommendation === "") {
      toast.error("Please select a recommendation.");
      return;
    }

    const updatedRecommendValue = newRecommendation === "true";

    fetch(`http://localhost:5500/logs/${id}`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ recommend: updatedRecommendValue }),
    })
      .then((res) => {
        if (!res.ok) {
          toast.error("Failed to update recommendation.");
        }
        return res.json();
      })
      .then((updatedLog) => {
        setLog(updatedLog);
        setNewRecommendation("");
        toast.success("Recommendation updated successfully!");
      })
      .catch(() => toast.error("Failed to update recommendation."));
  };

  if (!log) return <p>Loading log details...</p>;

  return (
    <div className="container">
      <h1>{isEditing ? "Edit Log" : "Log Details"}</h1>
      {isEditing ? (
        <form>
          <p>
            <strong>Restaurant:</strong> {getRestaurantName(log.restaurantId)}
          </p>

          <div className="mb-3">
            <label>Food Ordered</label>
            <input
              type="text"
              value={logRef.current.foodOrdered}
              onChange={(e) => (logRef.current.foodOrdered = e.target.value)}
              className="form-control"
            />
          </div>

          <div className="mb-3">
            <label>Date</label>
            <input
              type="date"
              value={logRef.current.date}
              onChange={(e) => (logRef.current.date = e.target.value)}
              className="form-control"
            />
          </div>

          <div className="mb-3">
            <label>Rating</label>
            <select
              value={logRef.current.rating}
              onChange={(e) => (logRef.current.rating = e.target.value)}
              className="form-select"
            >
              <option value="">Select Rating</option>
              {[1, 2, 3, 4, 5].map((rating) => (
                <option key={rating} value={rating}>
                  {rating}
                </option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label>Description</label>
            <textarea
              value={logRef.current.description}
              onChange={(e) => (logRef.current.description = e.target.value)}
              className="form-control"
            />
          </div>

          <div className="mb-3">
            <label>Dining Option</label>
            <div>
              {diningOptions.map((option) => (
                <label key={option.id} className="me-3">
                  <input
                    type="checkbox"
                    value={option.id}
                    checked={logRef.current.diningOption?.includes(option.id)}
                    onChange={(e) => {
                      const isChecked = e.target.checked;
                      if (isChecked) {
                        logRef.current.diningOption.push(option.id);
                      } else {
                        const index = logRef.current.diningOption.indexOf(
                          option.id
                        );
                        if (index !== -1)
                          logRef.current.diningOption.splice(index, 1);
                      }
                    }}
                  />
                  {option.name}
                </label>
              ))}
            </div>
          </div>

          <button
            type="button"
            className="btn btn-success"
            onClick={handleSaveChanges}
          >
            Save Changes
          </button>
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => setIsEditing(false)}
          >
            Cancel
          </button>
        </form>
      ) : (
        <>
          <LogItem
            log={log}
            users={users}
            restaurants={restaurants}
            diningOptions={diningOptions}
          />
          <div className="mb-3">
            <div className="recommendation-options">
              <div>
                <label>
                  <input
                    type="radio"
                    name="recommend"
                    value="true"
                    checked={newRecommendation === "true"}
                    onChange={(e) => setNewRecommendation(e.target.value)}
                  />{" "}
                  Recommend
                </label>
              </div>
              <div>
                <label>
                  <input
                    type="radio"
                    name="recommend"
                    value="false"
                    checked={newRecommendation === "false"}
                    onChange={(e) => setNewRecommendation(e.target.value)}
                  />{" "}
                  Don't Recommend
                </label>
              </div>
            </div>
            <br></br>

            <button
              className="btn btn-primary mt-2"
              onClick={handleUpdateRecommendation}
            >
              Update Recommendation
            </button>
          </div>

          <button
            className="btn btn-warning me-2"
            onClick={() => setIsEditing(true)}
          >
            Edit
          </button>

          <button
            className="btn btn-danger me-2"
            onClick={() => {
              if (window.confirm("Are you sure you want to delete this log?")) {
                fetch(`http://localhost:5500/logs/${id}`, { method: "DELETE" })
                  .then(() => {
                    toast.success("Log deleted successfully!");
                    navigate("/home");
                  })
                  .catch(() => toast.error("Failed to delete log."));
              }
            }}
          >
            Delete
          </button>

          <button
            className="btn btn-primary mt-3"
            onClick={() => navigate(`/logs/${id}/comments`)}
          >
            View Comments
          </button>
        </>
      )}
    </div>
  );
};

export default LogDetailsPage;
